import React, { useContext, useState, useEffect } from 'react';  
import proyectoContext from '../../context/proyectos/proyectoContext';
import tareaContext from '../../context/tareas/tareaContext';

const FormTarea = () => {

    // Extrar si un proyecto esta activo
    const proyectosContext = useContext(proyectoContext);
const { proyecto } = proyectosContext;

    // obtener la funci贸n del context de tarea
    const tareasContext = useContext(tareaContext);
    const {   errortarea, agregarTarea, validarTarea, obtenerTareas, actualizarTarea,tareaseleccionada } = tareasContext;
// Effect que detecta si hay una tarea seleccionada
    useEffect(() => {
        if(tareaseleccionada !== null) {
            guardarTarea(tareaseleccionada)
        } else {
            guardarTarea({
                nombre: ''
            })
        }
    }, [tareaseleccionada]); 
    

    // State del formulario
    const [tarea, guardarTarea] = useState({
        nombre: ''
    })

    // extraer el nombre del proyecto
    const { nombre } = tarea;

    // Si no hay proyecto seleccionado
    if(!proyecto) return null;

    // Array destructuring para extraer el proyecto actual
    const [proyectoActual] =  proyecto;

    // Leer los valores del formulario
    const handleChange = e => {
        guardarTarea({
            ...tarea,
            [e.target.name] : e.target.value
        })
    }

    const onSubmit = e => {
        e.preventDefault();

        // validar
        if(nombre.trim() === '' ) {
            validarTarea();
            return;
        }
          //agregar tarea
        
        // Si es edici贸n o si es nueva tarea
       if(tareaseleccionada === null ) {
            // agregar la nueva tarea al state de tareas
            tarea.proyectoId=proyectoActual.id;

            tarea.estado=false;
           agregarTarea(tarea);
        } else {
            // actualizar tarea existente
            actualizarTarea(tarea);

          
        }
        

        // Obtener y filtrar las tareas del proyecto actual
        obtenerTareas(proyectoActual.id);
        // reiniciar el form
        guardarTarea({
            nombre: ''
        })
    }

    return ( 
        <div className="formulario">
            <form
                onSubmit={onSubmit}
            >
                <div className="contenedor-input">
                    <input 
                        type="text"
                        className="input-text"
                        placeholder="Nombre Tarea..."
                        name="nombre"
                        value={nombre}
                        onChange={handleChange}
                    />
                </div>

                <div className="contenedor-input">
                    <input 
                        type="submit"
                        className="btn btn-primario btn-submit btn-block"
                        value={tareaseleccionada ? "Editar Tarea" : "Agregar Tarea"}
                    />
                </div>
            </form>
       {errortarea ? <p className="mensaje"> Coloque el nombre de la tarea</p>: null}
           
        </div>
     );
}
 
export default FormTarea;
