import React, { useReducer } from 'react';
import authContext from './authContext';
import authReducer from './authReducer';
import tokenAuth from '../../config/tokenAuth';
//axios para comunicarnos con el backend
//import clienteAxios from '../../config/axios';
import { REGISTRO_EXITOSO,REGISTRO_ERROR,OBTENER_USUARIO,LOGIN_EXITOSO,LOGIN_ERROR,CERRAR_SESION} from '../../types';



const AuthState= props=>{

    const initialState={
       token:localStorage.getItem('token'),
       autenticado:null,
       usuario:null,
       mensaje:null
    }

    //dispath para ejecutar las acciones
    const [state,dispath]= useReducer(authReducer,initialState);
 

    const registrarUsuario=async(datos)=>{
       try{
          const respuesta= await clienteAxios.post('/api/usuarios', datos);
          dispath({
            type:REGISTRO_EXITOSO,
            payload:respuesta.data //es el token del servidor
        });
        //Obtener usuario autenticado
       usuarioAutenticado();

       }catch(error){ // el error me viene del backend
          const alerta={
              msg:error.response.data.msg,
              categoria:'alerta-error'
          }

          dispath({
              type:REGISTRO_ERROR,
              payload: alerta
          })
      }       
    
    }


    const usuarioAutenticado=async(datos)=>{
   
            const token= localStorage.getItem('token');
         if(token){
             //funcion para enviar el token por headers
             tokenAuth(token);


             try {
                const respuesta= await clienteAxios.get('/api/usuarios', datos);


                dispath({
                    type:OBTENER_USUARIO,
                    payload: respuesta.data.usuario
                });
             } catch (error) {
                const alerta={
                    msg:error.response.data.msg,
                    categoria:'alerta-error'
                }
      
                dispath({
                    type:LOGIN_ERROR,
                    payload: alerta
                });
             }
         }
     
     }
 
  

   
    return(
        <authContext.Provider
        value={{
            token:state.token,
            autenticado: state.autenticado,
            mensaje:state.mensaje,
            usuario:state.usuario,
            registrarUsuario
          
        }}
        >
  
            {props.children}
        </authContext.Provider>
  
    );
}

export default AuthState;
