import React, { useState,useReducer } from 'react';
import proyectoContext from './proyectoContext';
import proyectoReducer from './proyectoReducer';
import { VALIDAR_FORMULARIO,AGREGAR_PROYECTO,FORMULARIO_PROYECTO,OBTENER_PROYECTOS} from '../../types';
import uuid from 'uuid';


const ProyectoState= props=>{

    const proyectos=[
        {id:1,nombre:'proyecto1'},
        {id:2,nombre:'proyecto2'},
        {id:3,nombre:'proyecto3'}
    
    ];
    const initialState={
        proyectos:[],
       formulario:false
    }

    //dispath para ejecutar las acciones
    const [state,dispath]= useReducer(proyectoReducer,initialState);

    //SERIE DE ACCIONES PARA EL CRUD
    const mostrarFormulario=()=>{
        //funciones que madans a llamar el reducer
    dispath({
        //lo que se va a evaluar en el reducer
        type:FORMULARIO_PROYECTO
    })
    }
    //obtener los proyectos

    const obtenerProyectos=()=>{
        dispath({

            type:OBTENER_PROYECTOS,
            payload:proyectos
        })
    }
    //agregar nuevo proyecto
    const agregarProyecto=(proyecto)=>{
        proyecto.id=uuid.v4();

        //agregar el pryecto al staste con un dispath
        dispath({

            type:AGREGAR_PROYECTO,
            payload:proyecto
        })
    }

    //VALIDAR FORMULARIO
    const validarFormulario=()=>{
      
        //agregar el pryecto al staste con un dispath
        dispath({

            type:VALIDAR_FORMULARIO,
            
        })
    }
    return(
        <proyectoContext.Provider
        value={{
            proyectos:state.proyectos,
            formulario: state.formulario,
            agregarProyecto,
            mostrarFormulario,
            obtenerProyectos
        }}
        >
  
            {props.children}
        </proyectoContext.Provider>
  
    );
}

export default ProyectoState;
