import React,{useState} from 'react';
import {Link} from 'react-router-dom';
const Login = () => {
    const [datosLogin,guardarDatosLogin]= useState({
        email:'',
     password:''
    });
      
      const {email,password}=datosLogin;

    const onChange= e=>{
         guardarDatosLogin({
           ...datosLogin,
         [e.targe.name]:e.targe.value
            });
    }
    const onSubmit=e=>{
        e.preventDefault();
     //validacion

     //pasar al action
    }
    return ( 
    
    <div className="form-usuario">
        <div className="contenedor-form sonbra-dark">
            <h1>Iniciar Sesion</h1>
         <form
         onSubmit={onSubmit}>
           <div className="campo-form">
               <label htmlFor="email">Email</label>
               <input 
               type="email"
               id="email"
               name="email"
               placeholder="Tu Email"
               value={email}
               onChange={onChange} />
           </div> 
           <div className="campo-form">
               <label htmlFor="password">Password</label>
               <input 
               type="password"
               id="password"
               name="password"
               value={password}
               placeholder="Tu Password"
               onChange={onChange} />
           </div> 
           <div className="campo-form">
              
               <input 
               type="submit"
               value="Iniciar Sesion"
               className="btn btn-primario btn-block"
               />
           </div> 
         </form>
         <Link to={'/nueva-cuenta'} className="enlace-cuenta">
           Registrarse </Link>
        </div> 
    </div> 
    
    );
}
 
export default Login;
