import React,{useReducer} from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {TAREAS_PROYECTO,AGREGAR_TAREA,VALIDAR_TAREA,ELIMINAR_TAREA,ESTADO_TAREA,TAREA_ACTUAL,ACTUALIZAR_TAREA} from '../../types';
import clienteAxios from '../../config/axiosCliente';


const TareaState= props=>{
    const initialState = {
       
        tareasproyecto:[],
        errortarea: false,
        tareaseleccionada: null
    }

    // Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //crear funciones

    const obtenerTareas=async(proyecto)=>{
        try {
     const resultado= await clienteAxios.get('/api/tareas',{params:{proyecto}});
            
        dispatch({
            type:TAREAS_PROYECTO,
            payload:resultado.data.tareas
        })
         } catch (error) {
      console.log(error);
         }
    }

    //AGREGAR TAREAS
    const agregarTarea=async(tarea)=>{
        console.log(tareasproyecto);
           try {
               const resultado=await clienteAxios.post('/api/tareas',tarea);
            dispatch({
                type:AGREGAR_TAREA,
                payload:tarea
           })
           } catch (error) {
               console.log(error);
           }
    }
    //ERROR
    const validarTarea=()=>{
        dispatch({
            type:VALIDAR_TAREA,
           
        })
    }

    //ELIMINAR TAREA
    const eliminarTarea=(tareaid)=>{
        dispatch({
            type:ELIMINAR_TAREA,
            payload:tareaid
        })
    }

    //cambia el estao de cada tarea
    const cambiarEstadoTarea=(tarea)=>{
        dispatch({
            type: ESTADO_TAREA,
            payload:tarea
        })
    }
    
    // ecxtraer una tarea para edicion
    const guardarTareaActual= tarea => {
        dispatch({
            type:TAREA_ACTUAL,
            payload:tarea
        })
    }

    // ecxtraer una tarea para edicion
    const actualizarTarea= tarea => {
        dispatch({
            type:ACTUALIZAR_TAREA,
            payload:tarea
        })
    }
return(
    <TareaContext.Provider
    value={{
       
        tareasproyecto:state.tareasproyecto,
        tareaseleccionada:state.tareaseleccionada,
        obtenerTareas,
        validarTarea,
        agregarTarea,
        eliminarTarea,
        cambiarEstadoTarea,
        guardarTareaActual,
        actualizarTarea,
        errortarea:state.errortarea
    }}
   >
        {props.children}
    </TareaContext.Provider>
)
}

export default TareaState;
