import React,{useReducer} from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {TAREAS_PROYECTO,AGREGAR_TAREA,VALIDAR_TAREA,ELIMINAR_TAREA,ESTADO_TAREA} from '../../types';


const TareaState= props=>{
    const initialState = {
        tareas: [ {id:1,nombre:'tarea del proyecto 1',estado:false,proyectoId:1},
        {id:2,nombre:'tarea del proyecto 1',estado:true,proyectoId:1},
        {id:3,nombre:'tarea del proyecto 2',estado:true,proyectoId:2},
        {id:4,nombre:'tarea del proyecto 3',estado:false,proyectoId:3}],
        tareasproyecto:null,
        errortarea: false,
        tareaseleccionada: null
    }

    // Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);

    //crear funciones

    const obtenerTareas=(id)=>{

        dispatch({
            type:TAREAS_PROYECTO,
            payload:id
        })
    }

    //AGREGAR TAREAS
    const agregarTarea=(tarea)=>{
            dispatch({
                   type:AGREGAR_TAREA,
                   payload:tarea
              })
    }
    //ERROR
    const validarTarea=()=>{
        dispatch({
            type:VALIDAR_TAREA,
           
        })
    }

    //ELIMINAR TAREA
    const eliminarTarea=(tareaid)=>{
        dispatch({
            type:ELIMINAR_TAREA,
            payload:tareaid
        })
    }

    //cambia el estao de cada tarea
    const cambiarEstadoTarea=(tarea)=>{
        dispatch({
            type: ESTADO_TAREA,
            payload:tarea
        })
    }
    
return(
    <TareaContext.Provider
    value={{
        tareas:state.tareas,
        tareasproyecto:state.tareasproyecto,
        obtenerTareas,
        validarTarea,
        agregarTarea,
        eliminarTarea,
        errortarea:state.errortarea
    }}
   >
        {props.children}
    </TareaContext.Provider>
)
}

export default TareaState;
